// src/store/createNetworkSlice.ts
var createNetworkSlice = (set, get) => ({
  networkStats: {
    last_updated: Date.now(),
    data: {}
  },
  bandwidthSaver: false,
  setBandwidthSaver: (value) => {
    set(() => ({ bandwidthSaver: value }));
  },
  setNetworkStats: (stats) => {
    const prevStats = get().networkStats;
    if (prevStats.last_updated > stats.last_updated) {
      return;
    }
    if (prevStats.data === stats.data) {
      return;
    }
    set(() => ({
      networkStats: stats
    }));
  }
});
var createNetworkSlice_default = createNetworkSlice;

export {
  createNetworkSlice_default
};
